import React from 'react';
import './Product.css'

const Product = (props) => {

    const {name,img,seller,price,stock} = props.product;
    return (
        <div className='product'>
            <div>
                <img src={img} alt="" srcset="" />
            </div>
            <div>
            <h4 className='product-name'>{name}</h4>
            <p><small>By:{seller}</small></p>
            <p>${price}</p>
            </div>
           
        </div>
    );
};

export default Product;
